import React, { useCallback, useEffect, useMemo, useState } from 'react';
import DayPicker, { DayModifiers } from 'react-day-picker';
import 'react-day-picker/lib/style.css';
import { isToday, format, parseISO, isAfter } from 'date-fns';
import ptBR from 'date-fns/locale/pt-BR';

import { useAuth } from '../../hooks/auth';

import { Container, Header, HeaderContent, Profile, Content, Schedule, NextAppointment, Section, Appointment, Calendar } from './styles'

import logoImg from '../../assets/logo.svg';

const Dashboard: React.FC = () => {

    return (
        <Container>
            <Header>
                <HeaderContent>
                    <img src={logoImg} alt="55PBX" />

                    <Profile>
                        <div>
                            <span>Bem-vindo,</span>
                            <strong> Acaciano Neves </strong>
                        </div>
                    </Profile>
                </HeaderContent>
            </Header>

            <Content>
                <Schedule>
                    <h1>Horários agendados</h1>
                    <p>
                        <span>Hoje</span>
                        <span>02</span>
                        <span>02</span>
                    </p>

                    {isToday(selectedDate) && nextAppointment && (
                        <NextAppointment>
                            <strong>Agendamento a seguir</strong>
                            <div>
                                <img src={'data:image/png;base64,' + nextAppointment.person.avatar} alt={nextAppointment.person.name} />

                                <strong>{nextAppointment.person.name}</strong>
                                <span>
                                    <FiClock />
                                    {nextAppointment.hourFormatted}
                                </span>
                            </div>
                        </NextAppointment>
                    )}

                    <Section>
                        <strong>Manhã</strong>

                        {morningAppointments.length === 0 && (
                            <p>Nenhum agendamento neste período</p>
                        )}

                        {morningAppointments.map(appointment => (
                            <Appointment key={appointment.id}>
                                <span>
                                    <FiClock />
                                    {appointment.hourFormatted}
                                </span>

                                <div>
                                    <img src={'data:image/png;base64,' + appointment.person.avatar} alt={appointment.person.name} />

                                    <strong> {appointment.person.name}</strong>
                                </div>
                            </Appointment>
                        ))}

                    </Section>

                    <Section>
                        <strong>Tarde</strong>

                        {afternoonAppointments.length === 0 && (
                            <p>Nenhum agendamento neste período</p>
                        )}

                        {afternoonAppointments.map(appointment => (
                            <Appointment key={appointment.id}>
                                <span>
                                    <FiClock />
                                    {appointment.hourFormatted}
                                </span>

                                <div>
                                    <img src={'data:image/png;base64,' + appointment.person.avatar} alt={appointment.person.name} />

                                    <strong> {appointment.person.name}</strong>
                                </div>
                            </Appointment>
                        ))}

                    </Section>

                    <Section>
                        <strong>Noite</strong>

                        {eveningAppointments.length === 0 && (
                            <p>Nenhum agendamento neste período</p>
                        )}

                        {eveningAppointments.map(appointment => (
                            <Appointment key={appointment.id}>
                                <span>
                                    <FiClock />
                                    {appointment.hourFormatted}
                                </span>

                                <div>
                                    <img src={'data:image/png;base64,' + appointment.person.avatar} alt={appointment.person.name} />

                                    <strong> {appointment.person.name}</strong>
                                </div>
                            </Appointment>
                        ))}
                    </Section>

                </Schedule>
                <Calendar>
                    <DayPicker
                        weekdaysShort={['D', 'S', 'T', 'Q', 'Q', 'S', 'S']}
                        fromMonth={new Date()}
                        disabledDays={[{ daysOfWeek: [] }, ...disabledDays]}
                        modifiers={{
                            available: { daysOfWeek: [0, 1, 2, 3, 4, 5, 6] },
                        }}
                        onMonthChange={handleMonthChange}
                        selectedDays={selectedDate}
                        onDayClick={handleDateChange}
                        months={[
                            'Janeiro',
                            'Fevereiro',
                            'Março',
                            'Abril',
                            'Maio',
                            'Junho',
                            'Julho',
                            'Agosto',
                            'Setembro',
                            'Outubro',
                            'Novembro',
                            'Dezembro',
                        ]}
                    />
                </Calendar>
            </Content>

        </Container>
    )
};

export default Dashboard;
