import React, { ChangeEvent, useCallback, useRef, useState } from 'react';
import { FiUser, FiMail, FiPhone, FiCamera, FiArrowLeft, FiLock } from 'react-icons/fi'
import { FormHandles } from '@unform/core';
import { Form } from '@unform/web';

import * as Yup from 'yup';

import api from '../../services/api';

import getValidationErrors from '../../utils/getValidationErrors';

import { Container, Content, AvatarInput } from './styles'

import Input from '../../components/Input';
import Button from '../../components/Button';
import { Link, useHistory } from 'react-router-dom';
import { useToast } from '../../hooks/toast';
import { useAuth } from '../../hooks/auth';
import ResponseBase from '../../models/ResponseBase';
import UserData from '../../models/UserData';

interface ChangePasswordFormData {
    name: string,
    email: string,
    cellPhone: string,
}

const ChangePassword: React.FC = () => {

    const [loading, setLoading] = useState(false);
    const formRef = useRef<FormHandles>(null);
    const { addToast } = useToast();
    const history = useHistory();

    const { user, updateUser } = useAuth();

    const handleSubmit = useCallback(async (data: ChangePasswordFormData) => {
        try {
            setLoading(true);
            formRef.current?.setErrors({});

            const schema = Yup.object().shape({
                currentPassword: Yup.string().required('Senha atual é obrigatória'),
                newPassword: Yup.string().required('Nova senha obrigatória'),
                confirmPassword: Yup.string().oneOf(
                    [Yup.ref('newPassword'), null], 'Nova senha e confirmação de senha não conferem.'
                ),
            });

            await schema.validate(data, {
                abortEarly: false
            });

            await api.post(`/v1/account/changePassword/logged`, data).then((response) => {
                updateUser(response.data.data);
            });

            history.push('/');

            addToast({
                type: 'success',
                title: 'Senha alterada!',
                description: 'Sua senha foi alterada com sucesso!.'
            });
        } catch (err) {

            if (err instanceof Yup.ValidationError) {
                const errors = getValidationErrors(err);

                if (errors) formRef.current?.setErrors(errors);

                return;
            } else {

                if (err.response.data.errors) {
                    for (var i = 0; i <= err.response.data.errors.length; i++) {
                        let message = err.response.data.errors[i];

                        if (message) {
                            addToast({
                                type: 'error',
                                title: 'Erro na alteração',
                                description: message,
                            });
                        }
                    }
                } else {
                    addToast({
                        type: 'error',
                        title: 'Erro na alteração',
                        description: 'Ocorreu um erro ao alterar a senha, tente novamente!'
                    });
                }
            }
        } finally {
            setLoading(false);
        }
    }, [addToast, history]);


    return (
        <Container>

            <header>
                <div>
                    <Link to="/dashboard">
                        <FiArrowLeft />
                    </Link>
                </div>
            </header>

            <Content>
                <Form ref={formRef} onSubmit={handleSubmit}>

                    <AvatarInput>
                        <img src={'data:image/png;base64,' + user.avatar} alt={user.name} />
                    </AvatarInput>

                    <h1>Alterar senha</h1>
                    <Input name="currentPassword" icon={FiLock} type="password" placeholder="Senha atual" />
                    <Input name="newPassword" icon={FiLock} type="password" placeholder="Nova senha" />
                    <Input name="confirmPassword" icon={FiLock} type="password" placeholder="Confirmar senha" />

                    <Button loading={loading} type="submit">Alterar senha</Button>
                </Form>
            </Content>
        </Container>
    )

};

export default ChangePassword;
